
     $.ajaxSetup({
        headers: {
            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
        }
    });
    time = 'Start !!';
    width = 0;
    success = 0;
    percent = 0;

    $(document).ready(function () {
        $(".question_number").first().trigger("click");
        questions_number = questions.length;
        quiz_time =$('#quiz_time').val()
        up_percent()
        countdown ();
    });
    $('.question_number').click(function (e) {
        this_id = $(this).attr('href')
        $('.collapse').not(this_id).collapse('hide')
        $('.collapse').on('hidden.bs.collapse', function () {
            $(this_id).collapse('show')
        })
        now_question = this.id;
        console.log(this_id);

    });
    $('.next').click(function (e) {
        e.preventDefault();
        index = jQuery.inArray(now_question, questions);
        next = questions[(index + 1) % questions.length];
        $("#" + next).trigger("click");
        if (!questions.length) {
           $('#form_all_question').submit();
        }
        console.log(next);

    });
    function delete_question(now){
        // console.log(questions)
        index = jQuery.inArray(now, questions);
        if(index!=-1){
            questions.splice(index, 1);
            now_question = questions[index-1]
        }else{
            // console.log(questions)
        }

    }
    $('.answer_radio').change(function (e) {
        $('#' + this.name + '_number').css('background-color', '#D7FADB');
        delete_question(this.name + '_number');

        up_percent()
        if (!questions.length) {
            $('.next').css('background','green')
            $('.next').addClass('btn-success')
            $('.next').text('ส่งคำตอบ')

        }
    });
    function countdown (){
        var downloadTimer = setInterval(function(){
            if(timeleft!='Start !!'){
                $('#timeleft').val(quiz_time-timeleft);
                minute =  parseInt(timeleft/60)
                sec = timeleft%60
                if(minute<10){
                    minute = '0'+minute
                }
                if(sec<10){
                    sec = '0'+sec
                }
                $("#countdown").html(minute + ' : ' + sec);
                $("#countdown").css('width',((100-((quiz_time - timeleft)/quiz_time)*100)+'%'))
            }else{
                $("#countdown").html("Start !!");
            }



            if(timeleft <= 0){
                clearInterval(downloadTimer);
                $("#countdown").html("Time Out");
                $('#submit_quiz').attr('disabled', 'true');
                $('#form_all_question').submit();

            }

            console.log(timeleft);

            timeleft = get_time();
        }, 1000);
    }

    function get_time() {
        $.ajax({
                type: "POST",
                url: $('#get_time').val(),
                cache: false,
                success: function (response) {
                    time =  response;
                }

            });
            return time;

    }


    function up_percent() {
        success = $('input:radio:checked').length;
        percen = parseInt(success / questions_number * 100);
        $('.progressbar-success').css('width', percen + "%");
        $('.progressbar-success').text(percen + "%");
    }
