const navSlide = () => {
    const burger = document.querySelector('.burger');
    const nav = document.querySelector('.nav-links');
    const navLinks = document.querySelectorAll('.nav-links li');

    burger.addEventListener('click', () => {
        //Toggle nav
        nav.classList.toggle('nav-active');

        //Animation links
        navLinks.forEach((link, index) => {
            if (link.style.animation) {
                link.style.animation = '';
            } else {
                link.style.animation = `navLinkFade 0.5s ease forwards ${index / 9 + 1}s`;
            }
        });

        //Burger Animation
        burger.classList.toggle('toggle');
    });

    $(function () {
        $(window).scroll(function () {

            var scrollTop = $(this).scrollTop();

            if (scrollTop != 0) {

                $('nav').stop().animate({
                    'opacity': '0.8'
                }, 400);

            } else {
                $('nav').stop().animate({
                    'opacity': '1'
                }, 400);
            }

        });

        $('nav').hover(
            function (e) {
                var scrollTop = $(window).scrollTop();
                if (scrollTop != 0) {
                    $('nav').stop().animate({
                        'opacity': '1'
                    }, 400);
                }
            },
            function (e) {
                var scrollTop = $(window).scrollTop();
                if (scrollTop != 0) {
                    $('nav').stop().animate({
                        'opacity': '0.8'
                    }, 400);
                }
            }
        );
    });

}

const navLeft = () => {
    const burger2 = document.querySelector('.burger2');
    const nav2 = document.querySelector('.nav-links2');
    const navLinks = document.querySelectorAll('.nav-links2 li');

    burger2.addEventListener('click', () => {
        //Burger Animation
        burger2.classList.toggle('toggle');
        //Toggle nav
        nav2.classList.toggle('nav-active2');
        //Animation links
        navLinks.forEach((link, index) => {
            // console.log(link);
            if (link.style.animation) {
                link.style.animation = '';
                link.style.animate = `opacity: 0`;
            } else {
                link.style.animation = `navLinkFade 0.5s ease forwards ${index / 5 + 1}s`;
            }
        });
    });
}


navLeft();
navSlide();
